mapper.select:
		SELECT * 
		FROM (
			SELECT ROWNUM RN, A.* 
				FROM ( SELECT * FROM FOODBOOKMARK
                       WHERE MEMBER_ID = #{member_id}
					 ) A
			 )
		WHERE RN BETWEEN #{start} AND #{end}

SELECT * FROM FOODBOOKMARK
WHERE MEMBER_ID = #{member_id}
AND BOOKMARK_ID = #{bookmark_id}

mapper.delete:
DELETE FROM FOODBOOKMARK 
WHERE MEMBER_ID = #{member_id}
AND BOOKMARK_ID = #{bookmark_id}
